
public class Employee {
	private int Id;
	private String Name;
	
	public Employee(int id, String name) {
		super();
		Id = id;
		Name = name;
	}
	public Employee() {
		// TODO Auto-generated constructor stub
	}
	public int getId() {
		return Id;
	}
	public void setId(int id) {
		Id = id;
	}
	public String getName() {
		return Name;
	}
	public void setName(String name) {
		Name = name;
	}
	
	

}
